INSERT INTO animals (codi, nomCientific, nomVulgar, descripcio, habitat, distribucio, rastre, fotoExcrement, fotoPetjada, fotoPetjades, fotoAnimal) VALUES (1, 'Crocidura russula', 'Musaraña común', 'Color grisáceo, poco contrastado entre la parte dorsal y el gris claro de la zona ventral. El color se oscurece en invierno y los juveniles presentan tonalidad gris en todo el cuerpo.', 'Asociado a muros de piedra, majanos u linderos que bordean pastizales, cultivos, corrales y afueras de pueblos. Se encuentra también en monte mediterráneo, robledales y choperas, hasta en prados montanos. En Sierra Nevada asciende hasta los 2000 metros, pero en la Cordillera Cantábrica no supera los 900 metros. Se distribuye en toda la península, excepto las cumbres más frías e Ibiza.', 'musaranyaDistri.png', '', 'musaranyaCaca.png', 'musaranyaPetjada.png', 'NULL', 'musaranya.png');