INSERT INTO animals (codi, nomCientific, nomVulgar, descripcio, habitat, distribucio, rastre, fotoPetjada, fotoAnimal) VALUES (1, 'Crocidura russula', 'Musaraña comú', 'Color grisenc, poc contrastat entre la part dorsal i el gris clar de la zona ventral. El color s&#39enfosqueix a l''hivern i els juvenils presenten tonalitat grisa en tot el cos.', 'Associat a murs de pedra, vedrunes o límits que voregen pastures, cultius, corrals i afores de pobles. Es troba també en muntanya mediterrània, rouredes i pollancredes, fins a prats montans. A Sierra Nevada ascendeix fins als 2000 metres, però a la Serralada Cantàbrica no supera els 900 metres. Es distribueix a tota la península, excepte els cims més fredes i Eivissa.', 'musaranyaDistri.png', '', 'musaranya_petjada', 'musaranya.png');
INSERT INTO animals (codi, nomCientific, nomVulgar, descripcio, habitat, distribucio, rastre, fotoPetjada, fotoAnimal) VALUES (2, 'Talpa occidentalis', 'Talp ibèric', 'Animal molt fàcil de reconèixer per si cos robust i cilíndric, musell rosat brillant, potes anteriors en forma de pala, absència de pavellons auriculars i dens pelatge negre vellutat, acabat en petita cua vertical. De cara llarg i estret, una mica repuntat cap amunt, manifesta major quantitat de pèls blancs en el musell i les mans cavadoras.', 'Espècie querenciosa de prats, cultius frescos i regats i sectors de bon sòl orgànic, ni sorrenc ni rocós. També resideix en boscos, més abundant en freixenedes, omedes i pollancredes que a les muntanyes de quercínies i pinedes, seleccionant àrees adehesadas i tàlvegs. El mateix resideix al litoral que arriba a cotes elevades a 1700 metres.', 'topilloDistri.png', 'Animals excavadors especialistes en devorar cucs, cucs blancs i miriàpodes, es detectes pels munts de terra remoguda a la fi de el sistema de túnels que habiten.', 'topillo_petjada', 'topillo.png');
INSERT INTO animals (codi, nomCientific, nomVulgar, descripcio, habitat, distribucio, rastre, fotoPetjada, fotoAnimal) VALUES (3, 'Erinaceus europaeus', 'Eriço fosc', 'Inconfusibles pel seu recobriment de pues i la facilitat d&#39enrotllar-si se&#39ls molesta. Aquestes pues espinoses tenen tons bruns cremosos amb una banda negra a prop de l&#39àpex i mesuren al dors, entre 2-3 cm de longitud. Els seus orejan no sobrepassen les puntes de les pues i es mes xato i fosc que l&#39Eriçó Moruno. Exhibeix una petita banda fosca entre musell i ulls.', 'Especialista de talls boscosos i taques arbustives que afronten amb àrees obertes de pasturatge i conreus. Viu des del nivell de la mar a el límit superior de l&#39arbrat. Més dependent de l&#39ambient ripari i els bosquets limítrofs a hortes i regadiu en els mitjans mediterranis. Hivernen entre octubre i març, menys temps en el sector mediterrani peninsular.', 'erizoEuropeoDistri.png', 'Característics excrements apepinados desmenuzables, compostos per cotxinilles de la humitat i insectes i llavors.', 'erizo_petjada', 'erizoEuropeo.png');
INSERT INTO animals (codi, nomCientific, nomVulgar, descripcio, habitat, distribucio, rastre, fotoPetjada, fotoAnimal) VALUES (4, 'Atelerix algirus', 'Eriço clar', 'Difícil de distingir de l&#39eriçó europeu, cal fixar-se en la seva petita talla, la seva coloració més clara i les seves orelles que sobresurten per sobre de les pues. Sembla més hocicudo, de front ample i límit recte entre els cabells ras i les pues. Nocturn i crepuscular, d&#39hàbits mal estudiats, sembla no tenir una hibernació generalitzada, observant individus actius durant tot l&#39any. Propens a realitzar els seus caus en huras de conills.', 'Viu al bosc i matoll termomediterrani ia les terrasses de cultius com ara oliveres, ametllers i garrofers.','erizoMorunoDistri.png', 'Característics excrements apepinados desmenuzables, compostos per cotxinilles de la humitat i insectes i llavors', 'erizo_petjada', 'erizoMoruno.png');
INSERT INTO animals (codi, nomCientific, nomVulgar, descripcio, habitat, distribucio, rastre, fotoPetjada, fotoAnimal) VALUES (5, 'Arvicola sapidus', 'Rata de aigua', 'Semiaquàtica, de cap camús, orelles arrodonides que gairebé no sobresurten del pelatge, dors marró fosc amb tonalitats vermelloses i ventre gris brut.', 'Espècie lligada a marges de cursos d&#39aigua de cabal lent, amb la vora excavables, i també en basses, llacunes i maresmes.', 'rataAguaDistri.png', 'Observable petits munts de cagades i acumulacions de trossos de joncs i bogues, tots de la mateixa mida, entre la vegetació palustre de marges de rierols i sèquies.', 'rataagua_petjada', 'rataAgua.png');
INSERT INTO animals (codi, nomCientific, nomVulgar, descripcio, habitat, distribucio, rastre, fotoPetjada, fotoAnimal) VALUES (6, 'Ondatra zibethica', 'Rata mesquera', 'Corpulent rosegador amfibi recognoscible per la seva densa capa marró fosca de la qual surten llargs pèls esquitxats, i pit platejat. Peus posteriors semipalmeados. El tret inconfusible és la forma de la cua, llarga i escamosa, negra i, aplanada lateralment. La seva altura és el triple que l&#39amplada. Excel·lent nedadora i bussejadora, vegetariana i nocturna. <br> <br> Va ser introduïda el 1905 a Txecoslovàquia, en granges pelleteres. Escapada de captivitat, ha ocupat gairebé tot Europa, convertint-se en plaga de rius i aiguamolls.', 'Especialista d&#39aiguamolls i vegetació palustre.', 'rataAlmizcleraDistri.png', 'Deixa passadissos d&#39uns 20 cm a la vegetació palustre i piles d&#39excrements, verds o negroses de la mida d&#39ossos d&#39olives, a la boca de les galeries, assortides de tubs de ventilació. Deixa típiques empremtes estavellades, de les cinc ungles, majors enrere (7 x 5 cm) que davant (3,5 x 3 cm).', 'rataalmizclera_petjada', 'rataAlmizclera.png');
INSERT INTO animals (codi, nomCientific, nomVulgar, descripcio, habitat, distribucio, rastre, fotoPetjada, fotoAnimal) VALUES (7, 'Apodemus sylvaticus', 'Ratóli de bosc', 'Dors marró grisenc i ventre blanc brut sense molt contrast, amb cua fina. Petita taca groguenca, allargada, a la gola. Ulls grans i negres molt prominents. <br> <br> Nocturn, encara que les femelles prenyades presenten una activitat diürna a l&#39estiu.', 'Rosegador molt adaptable, capaç d&#39habitar des de boscos de tot tipus de matolls, cultius, pastures, dunes i tota classe de límits. A les terres agrícoles es refugia en els boscos illa quan arriba l&#39hivern.', 'ratonCampoDistri.png', 'NULL', 'raton_petjada', 'ratonCampo.png');
INSERT INTO animals (codi, nomCientific, nomVulgar, descripcio, habitat, distribucio, rastre, fotoPetjada, fotoAnimal) VALUES (8, 'Rattus norvegicus', 'Rata parda', 'Robusta de cua una mica curta. Fàcil de distingir del Rat Negra pel seu musell bastant rom i orelles més curtes i gruixudes. Pelatge de color gris terrós, amb el ventre blanc brut o groguenc. <br> <br> Molt bona bussejadora, però no s&#39enfila com la Rata Parda. Esperança de vida curta, menys de 18 mesos, encara que amb un gran nombre de descendents anuals, al voltant de 56.', 'En poblacions urbanes s&#39alimenten de deixalles i viuen en baixos, soterranis i clavegueres. Al camp, resideix en llocs humits amb vegetació densa, sobretot en parcel·les de regadiu, arrossars, maresmes i vores de cursos d&#39aigua.', 'rataPardaDistri.png', 'Es pot diagnostiques l&#39espècie de rata per la grandària dels excrements, després de dividir el seu diàmetre per longitud, si el resultat és un valor de 0,42 a 0,56, la són de Rata Parda, i el seu és de 0,31-0 , 37 pertanyen a la Rata Negra.', 'rata_petjada', 'rataParda.png');
INSERT INTO animals (codi, nomCientific, nomVulgar, descripcio, habitat, distribucio, rastre, fotoPetjada, fotoAnimal) VALUES (9, 'Rattus rattus', 'Rata negra', 'Més petita i esvelta que la rata marró, amb musell punxegut, cua molt llarga i fina, i aspecte més orellut que la seva veïna. Coloració variable, generalment més fosca en ambients urbans i lleonats al camp.', 'Cosmopolita, però en les zones temperades és desplaçada per la Rat Per i s&#39acantona en cases. Tria en els nuclis urbans les golfes i altells de les cases, lluny dels soterranis i clavegueres que ocupa la seva competidora. En el camp viu en oliverars i bardisses. Fa nius tant a terra, entre arrels com a branques d&#39arbres perennes.', 'rataNegraDistri.png', 'Es pot diagnostiques l&#39espècie de rata per la grandària dels excrements, després de dividir el seu diàmetre per longitud, si el resultat és un valor de 0,42 a 0,56, la són de Rata Parda, i el seu és de 0,31-0 , 37 pertanyen a la Rata Negra.', 'rata_petjada', 'rataNegra.png');
INSERT INTO animals (codi, nomCientific, nomVulgar, descripcio, habitat, distribucio, rastre, fotoPetjada, fotoAnimal) VALUES (10, 'Eliomys quercinus', 'Rata cellarda', 'Els juvenils presenten gris dorsal i blanc brut ventral. La seva grandària augmenta cap al sud, i disminueix amb l&#39altitud. Es descriuen 4 subespècies espanyoles. La raça lusitanicus, de el sud-oest peninsular, de coloració més rogenca, raça quercinus, que ocupa la resta de l&#39àrea peninsular, amb un menor grandària, un colorit més pàl·lid i una cua ventralment blanca. A Mallorca i Menorca resideix gymnesicus, de pes mitjà i cua blanc. I a Formentera, resideix ophiusae, la subespècie més robusta, de color fosc i cua negra.', 'Rosegador generalista, molt versàtil. Freqüent en zones pedregoses però també en matoll dens i tota mena de boscos. Els agraden molt els llocs amb pedres i roques envoltats de vegetació.', 'lironDistri.png', 'Visibles els seus nius d&#39estiu fet de trossos de branques i fullatge.', 'liron_petjada', 'lironCareto.png');
INSERT INTO animals (codi, nomCientific, nomVulgar, descripcio, habitat, distribucio, rastre, fotoPetjada, fotoAnimal) VALUES (11, 'Sciurus vulgaris', 'Esquirol', 'Rosegador arborícola caracteritzat per la seva llarga i molt poblada cua, de pelatge dorsal vermellós i parts inferiors blanques. És un dels mamífers que presenta major varietat en coloracions. <br> <br> Espècie diürna, magnifica enfiladissa amb el seu colar ondulant. Descendeix del tronc cap per avall, en trajectòria sinuosa. Apeona de forma erràtica, amb la cua recte, fent freqüents parades en què vigila alçada sobre les cambres del darrere amb les orelles dretes i el musell olfateante. Intenta passar desapercebuda immobilitzant plana sobre l&#39escorça o situant-se en la cara d&#39el tronc amaga a l&#39observador.', 'Especialista forestal, adaptada a les frondoses eurosiberianes (roure, castanyer, hi hagi) i a tot tipus de coníferes (avet, pins, làrixs) i boscos mixtos. Pot colonitzar pinedes illa gràcies a la seva capacitat de travessar terrenys desarborats. No ocupa alzinars.', 'ardillaDistri.png', 'Molt cridanera, escoltant-de lluny al bosc un Castañeo (chuk - chuk - chuk). Fa nius en les copes dels arbres, en general a força alçada, més de 8 m, compactes, esfèrics, d&#39uns 30 cm de diàmetre, ancorats en una bifurcació de brancatge propera al fust.', 'ardilla_petjada', 'ardilla.png');
INSERT INTO animals (codi, nomCientific, nomVulgar, descripcio, habitat, distribucio, rastre, fotoPetjada, fotoAnimal) VALUES (12, 'Lepus granatensis', 'Llebre ibérica', 'De color marró ocre vermellós, entremesclat en negre al llom, i contrastat ventre blanc. La superfície blanca s&#39estén per les potes posteriors i anteriors.', 'Hàbitat molt variat, des de les maresmes i pastures litorals als prats d&#39alta muntanya. Més comú en camps de cereal, vinyes, oliveres i matoll esclarissat que en àrees de vegetació arbustiva o arbrada denses.', 'liebreDistri.png', 'Les llebres es detecten fàcilment per les seves excrements, majors (15 a 20 mm de diàmetre) que els de el conill i una mica aplanats, no totalment esfèrics, escampats o en petits cúmuls, però sense formar cagarruteros.', 'liebre_petjada', 'liebre.png');
INSERT INTO animals (codi, nomCientific, nomVulgar, descripcio, habitat, distribucio, rastre, fotoPetjada, fotoAnimal) VALUES (13, 'Oryctolagus cuniculus', 'Conill', 'Mida mitjana i color gris bru, amb una taca marró a la zona posterior de coll i ventre blanc brut. Cua bicolor gris dalt i blanc a baix. Orelles llargues de color marró uniforme, sense taca negra en les seves puntes. Ulls grans i negres. Els exemplars de nord peninsular són més grans que els de sud i les illes. <br> <br> Animals molt sedentaris que viuen en grups socials, compartint la seva vivar, el qual no s&#39allunyen més de 200 m.', 'L&#39hàbitat òptim a Espanya coincideix amb el clima mediterrani d&#39estiu, calorós i pluges d&#39uns 500mm, terreny ondulat, de sòl excavable i una combinació de muntanya (20%), matoll (30%), pasturatge (35%) i terreny nu (15%), amb un rierol proper i altitud inferior a 1000m.', 'conillDistri.png', 'Fàcil de detectar pos els cagarruteros, cúmuls d&#39excrements esfèrics i foscos (7 a 12 mm de diàmetre), de vegades propers a carrils que surten de les boques i per les escarbaduras superficials de terra que mesuren 5 a 15 cm de llarg, sense aprofundir més de 10 cm.', 'conill_petjada.', 'conill.png');
INSERT INTO animals (codi, nomCientific, nomVulgar, descripcio, habitat, distribucio, rastre, fotoPetjada, fotoAnimal) VALUES (14, 'Ursus arctos', 'Ós bru', 'Inconfusible per la seva gran grandària, cos rodanxó, orelles petites i arrodonides i cua molt curta. Color variable, des del vermellós marró fosc. Són freqüents les marques blanquinoses a la part alta de coll. Els óssos espanyols es caracteritzen per les potes negres i tenir groguenca la punta de els cabells. La femella és més fina que el mascle i pesa una quarta part menys. Els ossets solen presentar un collaret blanquinós que desapareix al créixer. <br> <br> Animal nocturn amb màxims d&#39activitat a l&#39enfosquir i alba. Es mou a gambada lent però pot córrer més ràpid que una persona. Àgil en terreny abrupte i capaç de enfilar-se als arbres.', 'Hivernen en un osil, habitualment una cavitat natural a penyes seques de quarsita, on confeccionen un niu de branques, bruc i herba. A el principi de la primavera, pasta herba tendra a les camperes de piornal. <br> <br> La mediocre qualitat de l&#39hàbitat en pirineus i les muntanyes cantàbriques origina una gran mobilitat i grans dominis vitals en els nostres óssos, dependents d&#39una ona estacional que, de primavera a l&#39hivern, passa d&#39herba tendra als nabius, amb freqüents tendències a la carronya.', 'osoDistri.png', 'Plantígrad delatat per empremtes inconfusibles que recorden a una trepitjada humana. Mà més curta i ampla que el peu, amb amplada que no sobrepassa els 18 cm. <br> <br> Excrements en forma de gran plasta, distingint-se, quan ha menjat herba, dels propis de vaques i cavalls per la seva olor dolça a melmelada i mínima trituració dels fragments vegetals.', 'oso_petjada', 'oso.png');
INSERT INTO animals (codi, nomCientific, nomVulgar, descripcio, habitat, distribucio, rastre, fotoPetjada, fotoAnimal) VALUES (15, 'Canis lupus', 'Llop ibèric', 'Subespècie caracteritzada per tenir tacades la part externa de la cua i la cara de negre, al igual que la cara anterior de les potes davanteres. Solen ser més foscos els exemplars de nord que els de sud. Amb pelatge més canyella a la zona ventral i potes, llarg i fosc coll, llom i cua. El colorit individual és variable. A l&#39estiu, al perdre l&#39esborra, semblen més esvelts i prims. <br> Costums nocturnes, capaç de recórrer entre 10 i 50 km per la nit. Viu en péquelas famílies compostes per la parella reproductora, les cries de l&#39any i els individus d&#39anys anteriors. Els membres d&#39aquestes rajades, generalment de menys de 10 individus, solen alimentar-se bé solitaris o en petits grups. Poc udoladors, potser adaptats a un mitjà molt humanitzat on sempre se&#39ls ha perseguit.', 'El llop és molt acomodatici i resideix en àmplia gamma de paisatges, des de la campanya gallega i les brañas cantàbriques a les pastures de cereal de secà amb alzinars prop del Duero, i les finques de muntanya mediterrània i cérvols de l&#39àrea d&#39Extremadura i Andalusia. La paridora passa d&#39abril a juny. La femella situa la lobera en enclavaments de vegetació densa propers a l&#39aigua, repoblacions tancades de pins, xaragalls, cingles i més rares vegades, en zorreras o tejoneras aïllades.', 'loboDistri.png', 'Empremtes més empaquetades i allargades que els gossos, amb coixinet interdigital gran, fortament escotada per darrere. <br> <br> Excrements cilíndrics més grans que els gossos (5 - 22 cm) i no retorçats. Pudents i contenen gran quantitat de pèls i ossos. Es trobes sobre matetes en creus de camins.', 'lobo_petjada', 'lobo.png');
INSERT INTO animals (codi, nomCientific, nomVulgar, descripcio, habitat, distribucio, rastre, fotoPetjada, fotoAnimal) VALUES (16, 'Vulpes vulpes', 'Rabosa', 'Cànid inconfusible per llarg i pelut cua horitzontal acabat en punta blanca. Orelles punxegudes, negres per fora, a l&#39igual que les calces. Pelatge bastant variable amb tonalitats vermelloses, groguenques i grises, fins i tot exemplars negres. N&#39hi ha amb barber blanc, platejat o negre. En època de zel, el barber de les femelles presenten tints rosats. <br> Activitat essencialment nocturna, entrant en activitat a la mitja hora de posar-se el sol, fins a les dues hores després de l&#39alba. Menja de tot, encara que les seves preses predilectes són ratolins i talpons al nord, i conills al sud.', 'Les majors densitats es donen en ambients heterogenis de bosquets, cultius, abocadors i granges, però és capaç de residir en zones subalpines i tot tipus de muntanyes. Ocupa tota la península però falta en balears i canàries.', 'zorroDistri.png', 'Marca unes petjades més llargues i ovalades que les de lso gossos petits, que imprimeixen els quatre dits i les ungles. <br> <br> Excrements de 5 a 20 cm de llarg, foscos i pestilents, més consistents que els canins, habitualment amb pèls que entrellacen els trossos de la cadena. Freqüentment sobre pedres, toperas o en els senders.', 'zorro_petjada', 'zorro.png');
INSERT INTO animals (codi, nomCientific, nomVulgar, descripcio, habitat, distribucio, rastre, fotoPetjada, fotoAnimal) VALUES (17, 'Mustela nivalis', 'Mostela', 'Diminut carnívor de cos cilíndric i curtes potes. Dors i cua de color marró clar i part ventral blanca des del llavi inferior a l&#39anus. Sovint s&#39aixeca sobre les potes del darrere. Les mosteles de nord peninsular (Subs. Vulgaris) presenten una línia ondulada, irregular, entre el mantell bru i el blanc ventral, a part d&#39una taca marró sota de la galta, mentre que els exemplars de sud de la península (Subs. Iberica) tenen una línia de separació recta, els peus blancs i no tenen taca bruna sota la galta.', 'Espècie estesa des del litoral a les pedregars d&#39alta muntanya, sent molt visible en murs de pedra, munts de llenya, tanques i vores de les cases en els llogarets.', 'comadrejaDistri.png', 'Excrements petits entre 3 - 5 cm de longitud i 2 - 3 cm de diàmetre retorçats, negres i acabats en petit floc pelut.', 'comadreja_petjada', 'comadreja.png');
INSERT INTO animals (codi, nomCientific, nomVulgar, descripcio, habitat, distribucio, rastre, fotoPetjada, fotoAnimal) VALUES (18, 'Mustela putorius', 'Turó', 'Típica màscara blanquinosa que abasta el musell, mentó i sengles bandes després dels ulls. Pelatge marró fosc en el llom, més clar en els flancs i negre a la panxa. A l&#39estiu són molt més foscos que a l&#39hivern. <br> <br> El mascle gairebé dobla en grandària a la femella. Marxa a salts amb l&#39esquena arquejada i freqüents postures alçades sobre les potes posteriors. Molt cridaner amb gran varietat de grunyits i espetecs quan s&#39alarma.', 'Adaptable a diversos ambients, però més comú en vores de rierols o en muntanyes amb cats de conills (conjunt de túnels creats pels conills).', 'turonDistri.png', 'Distància de salts curta, de 40 - 60 cm. <br> <br> Excrements negres i llargs, de fins a 7 cm, retorçats i d&#39extrems afilats. Dipositats en latrines al costat de les caus.', 'turon_petjada', 'turon.png');
INSERT INTO animals (codi, nomCientific, nomVulgar, descripcio, habitat, distribucio, rastre, fotoPetjada, fotoAnimal) VALUES (19, 'Martes martes', 'Marta', 'Barber groc o ataronjat no dividit. Pelatge marró fosc i nas negre. La esborra dels flancs és fosca. Peus molt peluts. Diferenciable de tots els mustèlids per les seves extremitats. <br> <br> Enfiladissa agilísima i curiosa que ascendeixo pels troncs abraçant com l&#39esquirol. Capaç de tirar-se a terra des copes de 20 metres d&#39altura. Caça mes a terra que en els arbres. Es encarna en nius vells d&#39esquirols i rapinyaires, poques vegades en tejoneras.', 'Resideix en boscos eurosiberians el mateix en coníferes, frondoses que en rodals mixtes. Prefereix arbrat madur, fins als 2000 m d&#39altitud a les muntanyes del nord peninsulars. En balears ocupen pineda blanc, alzina, savinar i garrigues.', 'martaDistri.png', 'Excrements d&#39olor mes dolç però sense aroma almesc. Molt retorçats i d&#39extrems afilats. Es dipositen en les pistes forestals sobre pedres o protuberàncies cada 100 - 200 metres, mai en latrines.', 'marta_petjada', 'marta.png');
INSERT INTO animals (codi, nomCientific, nomVulgar, descripcio, habitat, distribucio, rastre, fotoPetjada, fotoAnimal) VALUES (20, 'Martes foina', 'Fagina', 'Caracteritzada per la taca blanca que des del coll baixa i es bifurca fins a l&#39arrencada de les potes davanteres. Més pesada, corpulenta i paticorta que el mart, amb orelles petites i estretes, peus poc peluts i musell més aplatat. Colorit que varia del marró fosc al vermellós, aquest últim més freqüent en exemplars meridionals.', 'Animal molt adaptable, estès per tota la península, tant en boscos, matolls i roquissars com en ambients rurals i suburbans.', 'gardunyaDistri.png', 'Pota posterior lleugerament més llarg que les davanteres, amb la plantilla interdigital que es presenta bé quadriculada i formada per quatre o cinc blocs. <br> <br> Excrements llargs i retorçats, de tuf desagradable, afilats en un extrem, que diposita en latrines.', 'gardunya_petjada', 'gardunya.png');
INSERT INTO animals (codi, nomCientific, nomVulgar, descripcio, habitat, distribucio, rastre, fotoPetjada, fotoAnimal) VALUES (21, 'Lutra lutra', 'Llúdriga', 'Observada en general al costat de l&#39aigua, amb cos fusiforme i curtes potes palmades i llarga cua, ampla a la base i estreta a la punta. Pelussera curta i bruna, passant a gris brut a la cara ventral i blanquinosa a la gola. Mullat pelatge sembla llis i suau, però a l&#39assecar-se s&#39aglutinen els flocs i resulta una mica esfilagarsat. <br> <br> Busseig silenciós i elegant, que solen durar entre 10 i 40 segons. Observa amb els dos ulls i el nas sobre la làmina d&#39aigua. Es mou en terra amb aspecte geperut i amb la gambada a salts en curtes distàncies. Caràcter juganer, sent comú que tregui i manipuli cants arrodonits després de les capbussades.', 'Viu en rius, embassaments, aiguamolls i, unes poques, a la costa. Requereix peixos, tranquil·litat moderada i ribes que conservin el soto amb baix grau de contaminació.', 'nutriaDistri.png', 'Petjades de forma arrodonida que marquen els cinc dits i les petites ungles, gairebé circular la davantera i una mica més allargada la del darrere. Camina a salts. <br> <br> Excrements que apareixen en pedres grans, arrels d&#39arbres corpulents, esculleres i sota pilars de ponts, compostos per escates i espines de peixos, amb restes de crancs, que es desfan al agafar-los, d&#39olor dolça.', 'nutria_petjada', 'nutria.png');
INSERT INTO animals (codi, nomCientific, nomVulgar, descripcio, habitat, distribucio, rastre, fotoPetjada, fotoAnimal) VALUES (22, 'Meles meles', 'Teixó', 'Constitució ampla i robusta amb cap blanc creuada per bandes negres laterals que incorporen la zona ocular. Observat a distància sembla gris, color fruit d&#39un pèl blanc a la base i àpex, i de banda negra al centre. Potes i ventre coberts per pèl negre bastant esclarissat a l&#39abdomen. <br> <br> Es mou al trot amb el cap baix i les cambres del darrere balancejant d&#39un costat a un altre, alternant amb parades per escoltar.', 'Tejoneras de moltes boques i complicada xarxa de galeries i càmeres. Es diferencia de les zorreras per veure fora munts de terra i vegetals secs, resultat de la renovació dels llits. Viuen en tot tipus de muntanyes i deveses, encara que no els impedeix la residència en àrees cultivades, campiñas, riberes o penya-segats. Més nombrosos en mosaics de bosc amb cultius i pastures.', 'tejonDistri.png', 'Petjada inconfusible per marcar els 5 dits i les files de les urpes cap endavant, oberts en un arc estret. <br> <br> Excrements cilíndrics més menyspreables que els de el gos i dipositats en petites latrines excavades, tant a prop del cau com en les vores del domini vital.', 'tejon_petjada', 'tejon.png');
INSERT INTO animals (codi, nomCientific, nomVulgar, descripcio, habitat, distribucio, rastre, fotoPetjada, fotoAnimal) VALUES (23, 'Herpestes ichneumon', 'Mangosta africana', 'Cap fina, cos allargat, camacurt i cua singular, llarga i poblada, ampla en la seva arrencada i acaba en un floc de fortes truges negroses. Pelatge negre puntejat en groc. Arpa no retràctils. Pupil·la horitzontal, tret excepcional entre els carnívors. <br> <br> És el carnívor espanyol més diürn, molt visible en el seu deambular desmanegat, reptilià, sense apreciar-se les potes, ocultes pel llarg pèl penjant. Ni solitari ni social, és freqüent veure la mare seguida d&#39una a tres cries.', 'Típic de la muntanya mediterrània d&#39arboç, olivetes, llentiscles i estepes amb alzines i sureres. A les zones de camp i cultius es refugia en els sotos coberts de freixes, baladres i esbarzers. Caçador generalista, sobretot de pífies i rèptils.', 'meloncilloDistri.png', 'Petjades semiplantígradas (5 - 6 cm de llarg per 3 - 4 cm d&#39ample) que marquen els cinc dits, i en sòl sorrenc, deixen enrere l&#39estela de la cua. <br> <br> Excrements allargats (10 - 15 cm i 2 de diàmetre) i negres, dipositats en latrines en camins o vores de bardisses, moltes vegades amb pèls jaspiats blanquinegres de el propi exemplar.', 'meloncillo_petjada', 'meloncillo.png');
INSERT INTO animals (codi, nomCientific, nomVulgar, descripcio, habitat, distribucio, rastre, fotoPetjada, fotoAnimal) VALUES (24, 'Genetta genetta', 'Geneta', 'Aspecte gràcil i allargat, mes prima que un gat. Colorit marró grisenc ple de motes negres en el cos, que de vegades s&#39agrupen en franges longitudinals. Llarg cua amb anells foscos i clars. Morro afilat i orelles llargues, amb potes curtes. Arpes semirretráctiles. Morro molt contrastat, negre envoltat de nas i taca subocular blanca. <br> <br> Excel·lent enfiladissa, molt àgil i flexible, ajudada per la seva cua que actua com a timó estabilitzant. Bona nedadora. Molt bona vista que li permet la caça a l&#39aguait i la captura de preses mitjançant les seves urpes.', 'Espècie molt versàtil, el mateix habita en boscos i matolls que llocs rocosos. En les hortes cultivades es refugia en els sotos i les sèquies amb canyissos, bogues i salzes. Molt comú a les deveses d&#39alzines i sureres, i en les oliveres. Capaç d&#39adaptar al veïnatge de pobles, ralea en alta muntanya, encara que hi ha cites fins als 2.500 m d&#39altitud.', 'ginetaDistri.png', 'Petjades petites, rarament més de 4 x 4 cm, amb quatre coixinets, urpes sense marcar i una plantilla d&#39aspecte triangular formada per tres lòbuls fusionats. En sòl tou, es marca el cinquè dit, excèntric. <br> <br> Els seus excrements són gruixuts i llargs comparats amb la mida de l&#39animal. Apareixen doblegats en ferradura, de 20 cm de llarg i 2 cm d&#39ample, negrosos en fresc, d&#39olor no repel·lent i moltes vegades en grans cagarruteros sobre roques.', 'gineta_petjada', 'gineta.png');
INSERT INTO animals (codi, nomCientific, nomVulgar, descripcio, habitat, distribucio, rastre, fotoPetjada, fotoAnimal) VALUES (25, 'Lynx pardinus', 'Linx ibèric', 'Felí endèmic de corpulència mitjana, potes llargues i curta cua, acabada en extrem negre. Cara inconfusible en la qual destaquen les patilles punxegudes, més desenvolupades en els adults, i els pinzells negres a les puntes de les orelles. Pelatge comú, esquitxat per taques negroses. <br> <br> Animal solitari, crepuscular i nocturn, més fàcil d&#39observar durant el dia en jornades plujoses d&#39hivern. Adults territorials, residents a dominis exclusius on no permeten la intrusió de cap exemplar del seu sexe.', 'Especialista d&#39la muntanya mediterrània tranquil que combina densa cobertura vegetal i clarianes amb herba per als conills, el seu principal aliment. Aquesta estructura d&#39un hàbitat poc humanitzat i en mosaic, de cobertura superior al 60%, es troba tant a la muntanya de pins, brucs i estepes de Doñana com en serres mediterrànies, fins a altituds de 1.300 metres.', 'linceDistri.png', 'Deixa típiques petjades en senderes, creus de camins i tallafocs, arrodonides, amb quatre dits oberts en arc que no marquen les urpes. L&#39amplada entre la vora externa i les plantilles digitals varia entre els 68 mm de mascles adultes i una mica més de 40 mm en femelles i cries. <br> <br> Les cagades del linx ibèric són llargues i rectes, punxegudes en ambdós extrems i de color gris cendra. Es componen d&#39ossos, dents i pèl gris de Conill, i les diposita, sense enterrar, en latrines veïnes d&#39orins i vegetació esgarrapada. Mesuren fins a 25 cm de llarg.', 'lince_petjada', 'lince.png');
INSERT INTO animals (codi, nomCientific, nomVulgar, descripcio, habitat, distribucio, rastre, fotoPetjada, fotoAnimal) VALUES (26, 'Felis silvestris', 'Gat salvatge', 'Sempre més fort i corpulent que el gat domèstic,, amb ratllat fosc vertical i cua molt gruixuda, acabada en porra negra. Color de l&#39pelatge gris, amb una sola banda negra al llarg de l&#39espinada i braços recorreguts per dues franges negres. A l&#39contrari que els gats casolans, mai presenta motes negres ni vermelloses, i les seves marques blanques es restringeixen a la maixella, gola i zona sota el cul. <br> <br> Molt actiu a l&#39alba i al l&#39ocàs, i molt bones possibilitats d&#39observar-quan s&#39escalfa amb els primers raigs de sol a la partió del matoll amb clars. Els terrenys de caça corresponen a zones obertes de camperes de matoll, prats i talls boscosos.', 'Hàbitat variat, poc humanitzat, sobretot a les vores de taques de frondoses de baixa i mitja muntanya, freqüentment enllitat a matoll dens i afloraments rocosos. Estès a tot Espanya, encara que falta en les franges costaneres urbanitzades i en el secà desarborat.', 'gatDistri.png', 'Petjades típiques, de 6 cm de llarg i 5 cm d&#39ample, amb 4 coixinets davanteres i una plantilla interdigital gran trilobulada. Les petjades dels gats domèstics no superen els 4 x 4 cm i semblen més quadrades. <br> <br> Els seus excrements, a diferència del linx ibèric, són de 4 a 8 cm de llarg i 1,5 cm de diàmetre, cilíndriques, una mica recargolades i apuntades en els seus extrems, més sovint sobre objectes prominents (pedres, macollas) que en petites latrines obertes.', 'gat_petjada', 'gat.png');
INSERT INTO animals (codi, nomCientific, nomVulgar, descripcio, habitat, distribucio, rastre, fotoPetjada, fotoAnimal) VALUES (27, 'Sus scrofa', 'Porc senglar', 'Facilíssim de reconèixer pel seu cos rabassut, cap rematada en penques, potes curtes i petit cua acabat en floc de pèls negres, deixant en l&#39ambient un tuf inconfusible. Es poden apreciar tres classes d&#39edat. Amb menys de 6 mesos són ratllades, amb 11 típiques ratlles negres sobre fons groguenc. Els porcells de 6 a 12 mesos, cap curt, pelussera rogenca i cua sense floc. I amb més d&#39un any són adults, proveïts de llargues truges i una crinera dorsal que recorre tot el llom. Pelatge hivernal més llarg i abundant, amb densa esborra. <br> <br> Animal estrictament nocturn. Forma porcades integrades per femelles i descendents. Els mascles adults tendeixen a la vida solitària, de vegades acompanyats d&#39un altre subadult, anomenat escuder.', 'Espècie polivalent, en principi ben adaptada a l&#39ambient forestal, capaç d&#39acomodar també a bosquets, riberes i cultius de regadiu.', 'jabaliDistri.png', 'Petjades inconfusibles a l&#39marcar les dues peülles principals, i, darrere, les dues laterals. Amics de arrebossar en el fang, creen excavacions de 1,5 m de llarg i mig metre de profund, on es rebolquen abans de refregar en algun tronc proper. <br> <br> Excrement pudent i fosc, a partir de plastes circulars (4 - 5 cm de diàmetre) que s&#39agrupen en forma de botifarra. Gruny i Esbufega quan busca menjar. Amenaçat, fa petar agressivament la dentadura.', 'jabali_petjada', 'jabali.png');
INSERT INTO animals (codi, nomCientific, nomVulgar, descripcio, habitat, distribucio, rastre, fotoPetjada, fotoAnimal) VALUES (28, 'Ovis orientalis', 'Mufló', 'Carnero mediterrani de pèl curt i color marró xocolata, amb taques blanques en els flancs (només en els mascles), potes, cara i regió cabal. El mascle posseeix uns grans banyes corbades en cercle que creixen i afegeixen medrones amb l&#39edat. <br> <br> Espècie diürna, molt activa a l&#39alba i al crepuscle. Conducta social, en ramats separats durant primavera i estiu: femelles i joves d&#39una banda i mascles per un altre.', 'Accepta diversos tipus d&#39hàbitat, encara que en el seu origen prefereix matoll mediterrani amb roques. També s&#39ha adaptat a les pinedes d&#39Andorra i a us pinedes i codesales canaris en les Cañadas de Teide. Aquí amb impacte negativa sobre la flora autòctona.', 'muflonDistri.png', 'Petjades gairebé idèntiques a les de l&#39ovella, de dues peülles amb les puntes clarament separades (6 cm de llarg i 4,5 cm d&#39ample). <br> <br> Produeix masses d&#39excrements de 10 a 15 cm de llarg, les unitats adopten formes de facetes rectes entre la plasta.', 'muflon_petjada', 'muflon.png');
INSERT INTO animals (codi, nomCientific, nomVulgar, descripcio, habitat, distribucio, rastre, fotoPetjada, fotoAnimal) VALUES (29, 'Capra pirenaica', 'Cabra salvatge', 'Espècie ibèrica endèmica. Pelatge grisenc a l&#39hivern i marró canyella en veuran, amb taques negres en els flancs i en les potes que incrementen la seva extensió amb l&#39edat. Mascle coronat per una vistosa lira, cornamenta que aconsegueix els 75 cm o més de curvatura, amb crinera i barba característiques. <br> <br> Realitzen moviments transhumants, cap a solanes i baixos de vessant a l&#39hivern, i més altor a l&#39estiu i tardor. Els mascles realitzen desplaçaments més acusats i ascendeixen a majors cotes.', 'És un ungulat amic de les zones escarpades de serra a altituds que oscil·len entre els 500m i els 2500m. El mateix habita boscos montans de pi, alzines o melojos que formacions arbustives, siguin piornales, bruguerars o cadequers de muntanya mediterrània.', 'cabraDistri.png', 'Petjades de les peülles 7 x 4 cm i cagades cilíndriques (20 x 10 mm) en els mascles i esfèriques (11 x 9 mm) en les femelles. <br> <br> Les cagallons són cilíndriques, poc adhesives.', 'cabra_petjada', 'cabra.png');
INSERT INTO animals (codi, nomCientific, nomVulgar, descripcio, habitat, distribucio, rastre, fotoPetjada, fotoAnimal) VALUES (30, 'Cervus elaphus', 'Cèrvol', 'Impressionant animal de pelatge vermellós a l&#39estiu i grisenc a l&#39hivern, adornat amb una arboradura ramificada en puntes. La cérvola mesura entre un terç i la meitat d&#39un mascle. L&#39aspecte i desenvolupament de la banya permeten definir diverses classes d&#39edat masculines. Fins als 6 -7 mesos, els cervatells no tenen cornamenta. D&#39aquesta edat fins a l&#39any, emergeixen al cap dos mínims pivots. Entre un i dos anys són varetos, i a l&#39any següent horquillones. A partir d&#39aquí, el cérvol vermell exhibeix una cornamenta, les puntes no tenen a veure amb l&#39edat.', 'Habita a mitjans molt variats, des dels boscos i matolls de muntanyes fresques, a les pinedes de repoblació, deveses o les serres mediterrànies.', 'ciervoDistri.png', 'L&#39empremta de les dues peülles és gran. La vora convex anterior de la seva punta externa contrasta amb el recte de la vora interna. La presència de l&#39cérvol vermell s&#39aprecia pels seus efectes en la vegetació, uns deguts a l&#39esporgada del brot principal, que achaparra pins, i altres al desprendre el correal de la banya fregant-la amb arbres joves. <br> <br> Presenta un excrement voluminós (2 cm de llarg i 1,5 cm de diàmetre), arrodonit per un àpex en la femella i amb pinta de gla al cérvol.', 'ciervo_petjada', 'ciervo.png');
INSERT INTO animals (codi, nomCientific, nomVulgar, descripcio, habitat, distribucio, rastre, fotoPetjada, fotoAnimal) VALUES (31, 'Capreolus capreolus', 'Cabriol', 'De la mida d&#39una cabra, i sense cua aparent, vermellós a proper i gris terrós a l&#39hivern, visible a distància per la culera blanca, amb aspecte de pilota doble al mascle i de cor invertit en la femella. Morro inconfusible de nassos negres i vora blanc en els llavis. <br> <br> Cuerna masculina que s&#39inicia en el corcino amb dos pivots de no més de 3 cm. A l&#39any de vida, la nova banya exhibeix dos penjadors tan altes com les orelles. Amb dos anys és un horquillón, d&#39astes bifurcades, i la tercera cap és i a la típica de tres puntes d&#39uns 35 cm de longitud.', 'Hàbitat forestal, sobretot en boscos que ofereixen prats, límits i camperes. El mateix freqüenta fagedes, rouredes i melojares que pinedes o riberes. Al pis termomediterrani selecciona barrancs frescos d&#39alzines, sureres i roures amb subvuelo de Madroñal, llentiscle i bruc.', 'corzoDistri.png', 'Petjades de les dues peülles molt estretes i allargades (4,5 cm de llarg i 3 cm d&#39ample) .Els mascles marquen el seu territori destrossant arbrets de 30 - 70 cm de terra. A peu dels arbrets deixa un sector de sòl rascat amb les mans. <br> <br> Excrements cilíndrics allargats (14 x 16 mm), punxeguts, negres i disposats en cúmuls.', 'corzo_petjada', 'corzo.png');
INSERT INTO animals (codi, nomCientific, nomVulgar, descripcio, habitat, distribucio, rastre, fotoPetjada, fotoAnimal) VALUES (32, 'Equus ferus caballus', 'Cavall salvatge', 'Presenta formes arrodonides, perfil subconvex, coll arquejat i cua baixa. La capa sol ser tord o castanya i la alçada similar a la del cavall domèstic. Els seus cascos i peülles són més amples, el que facilita el trànsit pels aiguamolls, i les seves crineres menys espesses i cridaneres que les del cavall domèstic. La seva rusticitat es deu a la selecció natural, no a la humana. <br> <br> Cal destacar que aquests cavalls no són animals solitaris, sinó que, com passa amb la gran majoria d&#39herbívors, viuen formant rajades o ramats d&#39un nombre no molt extens d&#39exemplars. Aquests ramats i rajades són denominats com harems i estan compostos entre quatre a vint individus.', 'No és fàcil presenciar als cavalls salvatges, ja que no són un animal molt comú que diguem. Les zones que han pres per hàbitat solen ser, per norma general, àrees amb abundants prats i prats, poblades per una vegetació perenne i bastant humida. Aquestes àrees es corresponen amb grans i àmplies planes obertes i buidades per tal d&#39observar en tot moment a possibles perills com els depredadors.', 'caballoDistri.png', 'Cascos, poden aparèixer amb i sense ferradura. Si està ferrat, en la petjada es veu només la impressió de la ferradura. Si està sense ferrar, deixa empremtes de grans dimensions i forma gairebé circular, amb una profunda incisió per la part posterior.', 'caballo_petjada', 'caballo.png');
INSERT INTO animals (codi, nomCientific, nomVulgar, descripcio, habitat, distribucio, rastre, fotoPetjada, fotoAnimal) VALUES (33, 'Canis lupus familiaris', 'Gos comú', 'Constitueix una subespècie de llop. El 2001, s &#39estimava que hi havia quatre-cents milions de gossos al món. La seva grandària o talla, la seva forma i pelatge és molt divers segons la raça. Posseeix una oïda i olfacte molt desenvolupats, sent aquest últim el seu principal òrgan sensorial. <br> <br> En comparació amb llops de mida, els gossos tendeixen a tenir el crani un 20% més petit i el cervell un 10% més petit. La seva dieta de sobres dels humans va fer que els seus cervells grans i els músculs mandibulars utilitzats en la caça deixessin de ser necessaris.', 'Espècie cosmopolita. Es troba en tot tipus d&#39ambients, tant en mitjans forestals, espais oberts, i fins i tot ciutats.', 'perroDistri.png', 'Mans de cinc dits en les extremitats anteriors i 4 dits en les posteriors. Les ungles del davant estan molt afilades i pròximes entre si, les quals es marquen molt bé. Petjada molt compacta, estant els coixinets digitals molt juntes. <br> <br> Les petjades de el gos són molt semblants a les de la guineu, però són més grans, encara que depèn de la raça. Presenten els coixinets digitals més grans i juntes que les de la guineu.', 'perro_petjada', 'perro.jpg');